    }}
  /> </center>
      </div>);
  }

  componentDidMount() {

    this.setState((state, props) => ({
      instance:  new FormEngine(loginForm)
    }));

fetch('http://localhost:8080/getModelsByTypeId/2')
.then(res => res.json())
.then(data => this.setState({data}));    
  }
}

export default FormDesigner
