import React, { Component } from 'react';
import loginForm from './loginForm';
import { FormEngine, Form } from "react-json-form-engine";



class FormDesigner extends Component { 
  constructor(props) {
    super(props);

    this.state = {
      data: [],
      loginForm: null
    };
  }
  componentDidMount() {
    this.instance = new FormEngine({
      id: "login_form",
      title: "Welcome to Foo!",
      sections: [
        {
          id: "section_1",
          title: "Login Section",
          subsections: [
            {
              id: "subsection_1",
              title: "Login",
              subtitle: "Please enter your credentials.",
              fields: [
                {
                  id: "user_name",
                  title: "Username",
                  type: "string"
                },
                {
                  id: "user_pass",
                  title: "Password",
                  type: "string"
                },
                {
                  id: "remember_me",
                  title: "Remember me",
                  type: "boolean"
                }
              ]
            }
          ]
        }
      ],
      decorators: {
        "user_pass": {
          component: {
            type: "password"
          }
        }
      }
    });
  }

  render() {
    return (<div className="App">
      <h1>The Form Designer</h1> 
      Available form models:
      <ul>
                {
                    this.state.data.map((item) => {
                       return <li key={item.id}>{item.name} </li>
                    })
                }
            </ul>
            <hr />
            <center>
            <Form
                    hideTitle
                    submitButtonLabel="Login"
                    instance={this.instance}
                    onSubmit={() => {
                   // console.log(this.instance.getModelValues()); // Get form responses
                   // console.log(this.instance.serializeModel()); // Serialize form responses
    }}
  /> </center>
      </div>);
  }

  componentDidMount() {

fetch('http://localhost:8080/getModelsByTypeId/2')
.then(res => res.json())
.then(data => this.setState({data}));    
  }
}

export default FormDesigner
