import React, { Component } from 'react';
import { FormEngine, Form } from "react-json-form-engine";



class FormDesigner extends Component { 
  constructor(props) {
    super(props);

    this.state = {
      data: [],
      formJson: null,
      formInstance: null
    };
  }

  setFormEngine() {
    this.setState((state, props) => ({
      formInstance: new FormEngine(this.state.formJson)
    }));
  }

  render() {
    return (<div className="App">
      <h1><b>The Form Designer</b></h1> 
      Available form models:
      <ul>
                {
                    this.state.data.map((item) => {
                       return <li key={item.id}>{item.name} </li>
                    })
                }
            </ul>
            <hr />
            <center>
            <Form
                    hideTitle
                    submitButtonLabel="submit"
                    instance={ this.state.formInstance }
                    onSubmit={() => {
                    console.log(this.state.formInstance.getModelValues()); // Get form responses
                    console.log(this.state.formInstance.serializeModel()); // Serialize form responses
    }}
  /> </center>
      </div>);
  }

  componentWillMount() {
//TODO: get this from the DB 
    this.setState((state, props) => ({
      formJson: {
        id: "login_form",
        title: "Welcome to Foo!",
        sections: [
          {
            id: "section_1",
            title: "Login Section",
            subsections: [
              {
                id: "subsection_1",
                title: "Test form",
                subtitle: "This is just a test form.",
                fields: [
                  {
                    id: "user_name",
                    title: "field 1",
                    type: "string"
                  },
                  {
                    id: "user_pass",
                    title: "field 2",
                    type: "string"
                  },
                  {
                    id: "remember_me",
                    title: "just some random checkbox",
                    type: "boolean"
                  },
                {
                  "id": "bool3",
                  "type": "boolean",
                  "title": "Radio (Block)",
                  "inline": false,
                  "options": [
                    {
                      "title": "Yes"
                    },
                    {
                      "title": "No"
                    }
                  ]
                }
                ]
              }
            ]
          }
        ],
        decorators: {
          "user_pass": {
            component: {
              type: "password"
            }
          }
        }
      }




  }));
    this.setFormEngine();
  }

  componentDidMount() {

    fetch('http://localhost:8080/getModelsByTypeId/2')
    .then(res => res.json())
    .then(data => this.setState({data})); 
    this.setFormEngine();

  }
}

export default FormDesigner
