import React, { Component } from 'react';
import loginForm from './loginForm';
import { FormEngine, Form } from "react-json-form-engine";



class FormDesigner extends Component { 
  constructor(props) {
    super(props);

    this.state = {
      data: [],
      formJson: loginForm
    };
  }

  getFormEngine() {
    return new FormEngine(this.state.formJson);
  };

  render() {
    return (<div className="App">
      <h1>The Form Designer</h1> 
      Available form models:
      <ul>
                {
                    this.state.data.map((item) => {
                       return <li key={item.id}>{item.name} </li>
                    })
                }
            </ul>
            <hr />
            <center>
            <Form
                    hideTitle
                    submitButtonLabel="Login"
                    instance={ this.getFormEngine() }
                    onSubmit={() => {
                    //console.log(this.instance.getModelValues()); // Get form responses
                    //console.log(this.instance.serializeModel()); // Serialize form responses
    }}
  /> </center>
      </div>);
  }

  componentDidMount() {

    fetch('http://localhost:8080/getModelsByTypeId/2')
    .then(res => res.json())
    .then(data => this.setState({data}));    

    this.setState((state, props) => ({
        formJson: {
          id: "login_form",
          title: "Welcome to Foo!",
          sections: [
            {
              id: "section_1",
              title: "Login Section",
              subsections: [
                {
                  id: "subsection_1",
                  title: "Login",
                  subtitle: "Please enter your credentials.",
                  fields: [
                    {
                      id: "user_name",
                      title: "Username",
                      type: "string"
                    },
                    {
                      id: "user_pass",
                      title: "Password",
                      type: "string"
                    },
                    {
                      id: "remember_me",
                      title: "Remember me",
                      type: "boolean"
                    }
                  ]
                }
              ]
            }
          ],
          decorators: {
            "user_pass": {
              component: {
                type: "password"
              }
            }
          }
        }
    }));





  }
}

export default FormDesigner
