import React, { Component } from 'react';
import { apiUrl } from '../../resources/apiUrl';
import { Navbar } from 'react-bootstrap';

class EntityDesigner extends Component { 
  constructor(props) {
    super(props);

    this.state = {
      data: [],
      inputs: ['input-0']
    };
  }

  render() {
    return (<div className="App">
      <h5><b>The Entity Designer</b></h5> 
      Available entity models:
      <ul>
        {
          this.state.data.map((item, key) => {
            return <li key={key}>{item.name} </li>
          })
        }
      </ul>
      <hr/>
      <Navbar>
        <Navbar.Collapse>
          <Navbar.Form pullLeft>        {this.state.inputs.map((item, key) => {
            return <input type="text" name={item} key={key}/>;
            })}
             <button onClick={ () => this.appendInput() }>
                   ADD
               </button>
          </Navbar.Form>
        </Navbar.Collapse>
      </Navbar>           
      </div>);
  }

  componentDidMount() {
    fetch(apiUrl.url.concat('getModelsByTypeId/1'))
    .then(res => res.json())
    .then(data => this.setState({data}));
  }

  appendInput() {
    var newInput = `input-${this.state.inputs.length}`;
    this.setState({ inputs: this.state.inputs.concat([newInput]) });
}
}

export default EntityDesigner
